// header photo shrink
$(window).scroll(function() {
	if ($(document).scrollTop() > 30) {
		$('header').addClass("shrink");
  } else {
  	//$('header').removeClass("shrink");
  }
});

// header photo shrink
$(document).ready(setTimeout(function(){$('header').addClass("shrink");
}, 2000));

//STICKY NAV
$(document).ready(function () {  
  var top = $('#navigation_bar').offset().top - parseFloat($('#navigation_bar').css('marginTop').replace(/auto/, 1));
  $(window).scroll(function (event) {
    // what the y position of the scroll is
    var y = $(this).scrollTop();

    // whether that's below the form
    if (y >= top) {
      // if so, ad the fixed class
      $('#navigation_bar').addClass('fixed');
    } else {
      // otherwise remove it
      $('#navigation_bar').removeClass('fixed');
    }
  });
});
